script:
var pacman = Snap("#pacman");
var dot1 = Snap("#dot1");
var dot2 = Snap("#dot2");

pacman.attr({
  fill: "#FFFF00"
});

pacman.animate({d: "M 100 100 A 20 20 0 1 0 120 120 L 130 130 A 10 10 0 1 1 110 110 Z"}, 1000, function() {
  pacman.animate({d: "M 250 100 A 20 20 0 1 0 270 120 L 280 130 A 10 10 0 1 1 260 110 Z"}, 1000);
});

pacman.style({
  animation: "pacman-open 1s linear infinite"
});
